package day63_Intermediate_DSA_Strings.assignments;

public class Q2_LargestPalindrome_Optimised_Approach {
    public static void main(String[] args) {
        String S = "";
        System.out.println(largestPalindrome(S));
    }

    public static int largestPalindrome(String A) {
        int length = 0;
        for (int i = 0; i < A.length(); i++) {
            //Odd length Palindrome
            length = Math.max(length,getPalLength(A, i, i));

            //Even length Palindrome
            length = Math.max(length, getPalLength(A, i, i + 1));
        }
        return length;
    }

    public static int getPalLength(String A, int i, int j) {
        while (i >= 0 && j < A.length() && A.charAt(i) == A.charAt(j)) {
            i--;
            j++;
        }
        return j - i - 1;
    }
}
